#include <iostream>
#include <vector>
#include <thread>

bool increase(std::bitset<128> &nonce) {
    for (int i = 0; i < nonce.size(); i++) {
        nonce[i] = !nonce[i];
        if (nonce[i]) {
            return true;
        }
    }
    return false;
}

